/*    C ECHO client using sockets*/#include<stdio.h> //printf#include<string.h>    //strlen#include<sys/socket.h>    //socket#include<arpa/inet.h> //inet_addr int main(int argc , char *argv[]){    int sock;    struct sockaddr_in server;    char message[1024] , server_reply[2000];         /*---- Create the socket. The three arguments are: ----*/    /* 1) Internet domain  2) Stream socket  3) Default protocol (TCP in this case) */        sock = socket(AF_INET , SOCK_STREAM , 0);        if (sock == -1)    {        printf("Could not create socket");    }    puts("Socket created");    /* Set IP address to localhost */     server.sin_addr.s_addr = inet_addr("127.0.0.1");    /* Address family = Internet */    server.sin_family = AF_INET;    /* Set port number, using htons function to use proper byte order */    server.sin_port = htons( 8888);     //Connect to remote server    if (connect(sock , (struct sockaddr *)&server , sizeof(server)) < 0)    {        perror("connect failed. Error");        return 1;    }         puts("Connected\n");         //keep communicating with server    while(1)    {        printf("Enter message : ");        scanf("%s" , message);                 //Send some data        if( send(sock , message , strlen(message) , 0) < 0)        {            puts("Send failed");            return 1;        }                 //Receive a reply from the server        if( recv(sock , server_reply , 2000 , 0) < 0)        {            puts("recv failed");            break;        }                 puts("Server reply :");        printf("%s\n",server_reply);    }         close(sock);    return 0;}
